import React from "react";
import { useState } from "react";

import "./MainTemp.css";

export default function MainTemp(props) {
  let temperature = Math.round(props.temp);
  let [temp, setTemp] = useState(0);
  function handleClickCelsius(event) {
    event.preventDefault();
    setTemp(Math.round(temperature));
  }
  function handleClickFarengeit(event) {
    event.preventDefault();
    setTemp(Math.round((temperature * 9) / 5 + 32));
  }
  if ((temp = 0)) {
    return (
      <div className="Units">
        <span className="MainTemp">{temperature}</span>
        <span>
          <a href="/" className="Celsius" onClick={handleClickCelsius}>
            째C |
          </a>
        </span>
        <span>
          <a href="/" className="Farengeit" onClick={handleClickFarengeit}>
            째F
          </a>
        </span>
      </div>
    );
  } else {
    return (
      <div className="Units">
        <span className="MainTemp">{temp}</span>
        <span>
          <a href="/" className="Celsius" onClick={handleClickCelsius}>
            째C |
          </a>
        </span>
        <span>
          <a href="/" className="Farengeit" onClick={handleClickFarengeit}>
            째F
          </a>
        </span>
      </div>
    );
  }
}
