import React from "react";
import MainImage from "./MainImage";
import { useState } from "react";
import axios from "axios";
import CurrentDate from "./CurrentDate";

import "./ForecastDays.css";

export default function ForecastDays(props) {
  let [loaded, setLoaded] = useState(false);

  function handleResponse(response) {
    setLoaded(true);
  }

  if (loaded) {
    return (
      <div className="ForecastDays">
        <div className="Card" id="Forecastbase">
          <span className="ForecastDaysDate"></span>
          <span className="ForecastDaysImage">
            <MainImage icon="03n" size={40} />
          </span>
          <span className="ForecastDaysTempMax">17° </span>
          <span className="ForecastDaysTempMin">10°</span>
        </div>
      </div>
    );
  } else {
    let apiUrl = `https://api.openweathermap.org/data/2.5/onecall?lat=${props.coords.lat}&lon=${props.coords.lon}&exclude=daily&appid=6643c7326a4c2a38838264a28531d97e&units=metric`;
    axios.get(apiUrl).then(handleResponse);
    return null;
  }
}
